#include <stdio.h>
#include <string.h>
#include <assert.h>

#include <shared/error.h>

#include <out/common.h>

void
out_indent(FILE *f, unsigned int depth)
{
	while (depth-- > 0) {
		fputc('\t', f);
	}
}

void
out_generated_by_lexi(FILE *f)
{
	fprintf(f,
		"\n/*"
		"\n * Automatically generated by %s version %s"
		"\n * Copyright terms for the input source also apply to this generated code."
		"\n */\n\n", progname, progvers);
}

